import React from 'react';
import TabsTop from '../components/TabsTop.jsx';
import DropMenu from '../components/DropMenu.jsx';
import TwoItem from '../components/TwoItem.jsx';
import ThreeItem from '../components/ThreeItem.jsx';
import FourItem from '../components/FourItem.jsx';
import DetailTop from '../components/DetailTop';
import BackBar from '../components/BackBar';
import ReactDOM from 'react-dom';
class App extends React.Component {
  constructor(props){
    super(props);
    this.scollNum=0;
    this.state={
      menuTop:338,
      isAlpha:false,
    }
    this.changeAlpha=false;
  }

  componentDidMount() {
     let topV=ReactDOM.findDOMNode(this.rightBtn).offsetTop;
     topV=topV+58;
     this.setState({menuTop:topV});
   }
   onScrollEvent=()=>{
    this.scollNum++;
    let thisNum=parseInt(this.scollNum,10);
      if(!this.state.isAlpha&&!this.changeAlpha){
        this.setState({isAlpha:true});
        this.changeAlpha=true;
      }
    setTimeout(()=>{this.changeAlpha=false;},300);
    setTimeout(()=>{  
      if(thisNum===this.scollNum){
        if(this.state.isAlpha)this.setState({isAlpha:false});
        }
     },1000);
    }
    render() {
      const { history,location }=this.props;
      const pageProps={history,location};
      console.log(this.state.isAlpha);
        return(
          <div className="App" onScrollCapture={() => this.onScrollEvent()}>
            <BackBar {...pageProps}/>
            <DetailTop {...pageProps}/>
            <br/>
            <TabsTop activeid={0} {...pageProps}/>
            <DropMenu {...pageProps} fixTop={this.state.menuTop} ref={leftB=>{this.rightBtn=leftB}} isAlpha={this.state.isAlpha}/>
            <TwoItem fixMain={true}/>
            <ThreeItem fixMain={true}/>
            <FourItem fixMain={true}/>
            <FourItem fixMain={true}/>
          </div>
        );
    }
}

export default App;
