import React from 'react';
import BackBar from '../components/BackBar';
import ReactDOM from 'react-dom';
import { api } from '../config/Api';
import { get } from '../utils/NetWorking';
import { Toast,ActivityIndicator,ListView } from 'antd-mobile';
import { getsearch } from '../utils/Help';
const EmptyList=()=>(<div>空白</div>)
const testdata = [
  {
    img: 'https://zos.alipayobjects.com/rmsportal/dKbkpPXKfvZzWCM.png',
    title: 'Meet hotel',
    des: '不是所有的兼职汪都需要风吹日晒',
  },
  {
    img: 'https://zos.alipayobjects.com/rmsportal/XmwCzSeJiqpkuMB.png',
    title: 'McDonald\'s invites you',
    des: '不是所有的兼职汪都需要风吹日晒',
  },
  {
    img: 'https://zos.alipayobjects.com/rmsportal/hfVtzEhPzTUewPm.png',
    title: 'Eat the week',
    des: '不是所有的兼职汪都需要风吹日晒',
  },
];
class App extends React.Component {
  constructor(props){
    super(props);
    let searchA=getsearch(this.props.location);
    this.scollNum=0;
    this.state={
      menuTop:338,
      isAlpha:false,
      dataSource:[],

      loading:false,
    }
    this.changeAlpha=false;
    this.lock=false;
    this.loading=false;
    console.log(this.state);
  }

  componentWillUnmount(){
    this.lock=true;
  }
  componentDidMount() {
    this.setState({dataSource:this.state.dataSource.cloneWithRowsAndSections(testdata,[],[])});
   }


    render() {
      const { history,location }=this.props;
      const pageProps={history,location};
      let index = testdata.length - 1;
      const row = (rowData, sectionID, rowID) => {
        if (index < 0) {
          index = testdata.length - 1;
        }
        const obj = testdata[index--];
        return (
          <div key={rowID} style={{ padding: '0 15px' }}>
            <div
              style={{
                lineHeight: '50px',
                color: '#888',
                fontSize: 18,
                borderBottom: '1px solid #F6F6F6',
              }}
            >{obj.title}</div>
            <div style={{ display: '-webkit-box', display: 'flex', padding: '15px 0' }}>
              <img style={{ height: '64px', marginRight: '15px' }} src={obj.img} alt="" />
              <div style={{ lineHeight: 1 }}>
                <div style={{ marginBottom: '8px', fontWeight: 'bold' }}>{obj.des}</div>
                <div><span style={{ fontSize: '30px', color: '#FF6E27' }}>35</span>¥ {rowID}</div>
              </div>
            </div>
          </div>
        );
      };
        return(
          <div className="App">
            <BackBar {...pageProps} />
            <ListView
              ref={el => this.lv = el}
              dataSource={this.state.dataSource}
              renderHeader={() => <span>header</span>}
              renderFooter={() => (<div style={{ padding: 30, textAlign: 'center' }}>
                {this.state.isLoading ? 'Loading...' : 'Loaded'}
              </div>)}
               renderRow={row}
              onScroll={() => { console.log('scroll'); }}
              scrollRenderAheadDistance={500}
              />
          </div>
        );
    }
}

export default App;
