import React from "react";
import '../assets/css/CustomSelect.css';
import ArrowLeft from '../assets/icon/ArrowLeft';
import { Picker} from 'antd-mobile';
import { getsearch } from '../utils/Help';
class CustomSelect extends React.Component {
  constructor(props){
    super(props);
    const searchA=getsearch(window.location);
    let pdData=[
      {value:100,label:""},
      {value:200,label:""},
      {value:300,label:""},
      {value:400,label:""}
    ];
    pdData.unshift({value:searchA['price'],label:""});
    this.state={
      pickA:[this.props.dropActive?this.props.dropActive:pdData[0]['value']],
      pickData:pdData,
    };

  }

  changePick(index){
    this.props.dropFun&&this.props.dropFun(this.state.pickData[index]['value']);
    this.setState({pickA:[index]});
  }
  render() {
    let oklabel=this.state.pickData[this.state.pickA[0]]['value'];
    return (
      <Picker data={this.state.pickData} cols={1} title="自定义历史记录" onOk={(val)=>{
        this.changePick(val[0]);
      }} onDismiss={()=>{console.log("取消");}} value={this.state.pickA}>
      <div className="CustomSelectBox">
         <span >凑单金额：{oklabel}元</span><ArrowLeft width="15px" height="15px" fill="#888888"/>
      </div></Picker>
    );
  }
}

export default CustomSelect;
