import React from 'react';
import '../assets/css/CustomOrder.css';
import BackBar from '../components/BackBar';
import {InputItem,Toast } from 'antd-mobile';
import TagBtn from '../components/TagBtn';
import TagTwoBtn from '../components/TagTwoBtn';
import compact from 'lodash/compact';
class CustomOrder extends React.Component {
    constructor(props){
      super(props);
      this.state={
        tagData:[
          {title:"手机",id:1},
          {title:"运动",id:2},
          {title:"数码",id:3},
          {title:"家具",id:4},
          {title:"食品",id:5},
          {title:"彩妆",id:6},
          {title:"女装",id:7},
          {title:"电器",id:8},
          {title:"母婴",id:9},
        ],
        tagSta:[],
        inputvalue:"",
      }
    }
    changeTagFun=(v,sta)=>{
      let tagStaA=[...this.state.tagSta];
      tagStaA[v]=sta;
      this.setState({tagSta:tagStaA});
      //console.log(this.state.tagSta);
    }
    resetFun() {
      console.log("0000")
      this.setState({tagSta:[]});
    }
    submitFun=()=>{
      if(this.state.inputvalue===""){
        Toast.fail("请输入金额",1);
        return;
      }
      let tagStaA=compact(this.state.tagSta);
      if(tagStaA.length<1){
        Toast.fail("请选择商品种类",1);
        return;
      }
      let tagStr=this.getTagIds(this.state.tagSta);
      let req="/customresult?price="+this.state.inputvalue+"&tag="+tagStr;
      console.log(req);
      //this.props.history.push(req);
    }
    getTagIds(array){
      let tagA=[];
      for (let index = 0; index < array.length; index++) {
        const element = array[index];
        if(element)tagA.push(this.state.tagData[index]);
      }
      return tagA.join(",");
    }
    changeInput=(val)=>{
      this.setState({inputvalue:val});
    }
    render() {
       const { history }=this.props;
        let tagStaA=compact(this.state.tagSta);
        let isSetable=tagStaA.length>0?true:false;
        return(
          <div className="App">
             <BackBar history={history}/>
             <div className="CustomOrderTitle">凑单金额<span>(会在这个金额的10元左右浮动)</span></div>
             <div className="CustomOrderInput"><InputItem placeholder="输入还需要凑单的金额" value={this.state.inputvalue} onChange={this.changeInput} type="number" pattern="[0-9]*"/></div>
             <div className="CustomOrderTitle" style={{marginTop:"35px",}}>商品种类</div>
             <div className="CustomOrderTag">
               {this.state.tagData.map((item,index)=>(<div className="CustomOrderCrid" key={index}>
                 <TagBtn title={item.title} tagIndex={index} onChange={this.changeTagFun} isActive={this.state.tagSta[index]}/></div>))}
               <div className="CustomOrderSubmit"><TagTwoBtn resetAble={isSetable} reset={this.resetFun} submit={this.submitFun}/></div>
             </div>
          </div>
        );
    }
}

export default CustomOrder;
