import React from 'react';
import BackBar from '../components/BackBar';
import '../assets/css/CustomResult.css';
import CustomSelect from '../components/CustomSelect';
import ItemCuntom from '../components/ItemCuntom';
import { api } from '../config/Api';
import { get } from '../utils/NetWorking';
import { getsearch } from '../utils/Help';
import { Toast } from 'antd-mobile';
//const EmptyList=()=>(<div><div className="half"><ItemCuntom/></div><div className="half"><ItemCuntom/></div><div className="half"><ItemCuntom/></div><div className="half"><ItemCuntom/></div><div className="half"><ItemCuntom/></div><div className="half"><ItemCuntom/></div><div className="half"><ItemCuntom/></div></div>)
class App extends React.Component {
    constructor(props){
      super(props);
      const searchA=getsearch(this.props.location);
      this.state={
        nowprice:searchA['price']?searchA['price']:0,
        cids:searchA['tag'],
        dataSource:[],
        loading:false,
      }
      this.lock=false;
      this.loading=false;
      console.log(this.state);
    }
    dropFun=(price)=>{
      this.setState({nowprice:price});
    }
    componentDidMount(){
      this.netListdata(this.state.nowprice,this.state.cids);
    }
    componentWillUnmount(){
      this.lock=true;
    }
    loadingEnd(){
      this.loading=false;
      if(!this.lock)this.setState({loading:false});
    }
    loadingStart(){
      this.loading=true;
      this.setState({loading:true});
    }
    netListdata(price,cids){
     let req="?price="+price+"&cid_list="+cids;
     let thisUrl=api.cunstom_list+req;
      if(this.loading)return;
      this.loadingStart();
      get(thisUrl,(res)=>{
        this.loadingEnd();
        console.log(res);
        if(res&&res['result']==='success'){
          this.setState({dataSource:res['data']});
        }else{
          if(res&&res['message'])Toast.fail(res['message'], 1);
        }
      },(erro)=>{
        this.loadingEnd();
        console.log(erro);
      });
    }
    render() {
      const { history,location }=this.props;
        return(
          <div className="App">
            <BackBar history={history} title="自定义"/>
            <div className="CustomResultBox">
              <CustomSelect price={110} dropFun={this.dropFun} location={location}/>
              <div className="CustomResultTxt">购物津贴凑单是根据原价计算哦～<br/>
               可以以券后价入手，达到满减的要求哦～</div>
               <div className="CustomResultList">
                   <div className="half"><ItemCuntom/></div>
                   <div className="half"><ItemCuntom/></div>
                   <div className="half"><ItemCuntom/></div>
                   <div className="half"><ItemCuntom/></div>
                   <div className="half"><ItemCuntom/></div>
                   <div className="half"><ItemCuntom/></div>
               </div>
           </div>
          </div>
        );
    }
}

export default App;
