import React from "react";
import '../assets/css/TagBtn.css';
import classNames from 'classnames';
class TagBtn extends React.Component {
  state={
    isActive:false,
  }
  changeTag(){
    this.props.onChange&&this.props.onChange(this.props.tagIndex,!this.state.isActive);
    this.setState({isActive:!this.state.isActive});
  }
  render() {
    const TagBtnCss=classNames('TagBtnBox',{'TagBtnActive': this.state.isActive},{'TagBtnNormal': !this.state.isActive})
    return (
      <div className={TagBtnCss} onClick={()=>{this.changeTag();}}>
         {this.props.title}
      </div>
    );
  }
}

export default TagBtn;
