import React from 'react';
import SearchTop from '../components/SearchTop.jsx';
import TabsTop from '../components/TabsTop.jsx';
import DropMenu from '../components/DropMenu.jsx';
import TwoItem from '../components/TwoItem.jsx';
import ThreeItem from '../components/ThreeItem.jsx';
import FourItem from '../components/FourItem.jsx';
import {api} from '../config/Api';
import {get} from '../utils/NetWorking';
import { PullToRefresh, ListView, Toast} from 'antd-mobile';

const EmptyList=()=>(<div><TwoItem/><ThreeItem/><FourItem/></div>);
const separator = (sectionID, rowID) => (
  <div
    key={`${sectionID}-${rowID}`}
    style={{
      backgroundColor: '#F5F5F9',
      height: 8,
      borderTop: '1px solid #ECECED',
      borderBottom: '1px solid #ECECED',
    }}
  />
);//隔开

function genData(pIndex = 0) {
  const dataArr = [];
  for (let i = 0; i < NUM_ROWS; i++) {
    dataArr.push(`row - ${(pIndex * NUM_ROWS) + i}`);
  }
  return dataArr;
}

class App extends React.Component {
    constructor(props){
      super(props);
      const listData = new ListView.DataSource({
        rowHasChanged: (row1, row2) => row1 !== row2,
      });
      this.state={
        listData,
        refreshing: true,
        isLoading: true,
        height: document.documentElement.clientHeight,
        useBodyScroll: false,
      }
    }
    componentDidMount(){
      this.getListdata();
    }
    getListdata(){
      let thisUrl=api.index_list;
      get(thisUrl,(res)=>{
        if(res&&res['result']==='success'){
          this.setState({listData:res['data']});
        }else{
          if(res&&res['message'])Toast.fail(res['message'], 1);
        }
      },(erro)=>{
        console.log(erro);
      })
    }
  rowMC = (rowData, sectionID, rowID) => {
      console.log(rowData);
      console.log(sectionID);
      console.log(rowID);
      return (
        <div key={rowID}
          style={{
            padding: '0 15px',
            backgroundColor: 'white',
          }}
        >
          kkkk
        </div>
      );
    };
    onRefresh = () => {
      this.setState({ refreshing: true, isLoading: true });
      // simulate initial Ajax
      setTimeout(() => {
        this.rData = genData();
        this.setState({
          dataSource: this.state.dataSource.cloneWithRows(this.rData),
          refreshing: false,
          isLoading: false,
        });
      }, 600);
    };

    onEndReached = (event) => {
      // load new data
      // hasMore: from backend data, indicates whether it is the last page, here is false
      if (this.state.isLoading && !this.state.hasMore) {
        return;
      }
      console.log('reach end', event);
      this.setState({ isLoading: true });
      setTimeout(() => {
        this.rData = [...this.rData, ...genData(++pageIndex)];
        this.setState({
          dataSource: this.state.dataSource.cloneWithRows(this.rData),
          isLoading: false,
        });
      }, 1000);
    };

    render() {
        const { history,location }=this.props;
        const pageProps={history,location};

        return(
          <div className="App">
            <SearchTop  {...pageProps}/>
            <TabsTop activeid={0} {...pageProps}/>
            <DropMenu {...pageProps}/>
            {this.state.listData&&this.state.listData.length>0?
                <ListView
                key='1'
                ref={el => this.lv = el}
                dataSource={this.state.listData}
                renderHeader={() => <span>下拉刷新</span>}
                renderFooter={() => (<div style={{ padding: 30, textAlign: 'center' }}>
                  {this.state.isLoading ? '加载中...' : '已完成加载'}
                </div>)}
                renderRow={this.rowMC}
                renderSeparator={separator}
                useBodyScroll={false}
                style={{
                  height: this.state.height,
                  border: '1px solid #ddd',
                  margin: '5px 0',
                }}
                pullToRefresh={<PullToRefresh
                  refreshing={this.state.refreshing}
                  onRefresh={this.onRefresh}
                />}
                onEndReached={this.onEndReached}
                pageSize={1}
              />
              :<EmptyList/>}
          </div>
        );
    }
}

export default App;
