import React from 'react';
import TabsTop from '../components/TabsTop.jsx';
import DropMenu from '../components/DropMenu.jsx';
import TwoItem from '../components/TwoItem.jsx';
import ThreeItem from '../components/ThreeItem.jsx';
import FourItem from '../components/FourItem.jsx';
import DetailTop from '../components/DetailTop';
import BackBar from '../components/BackBar';
import ReactDOM from 'react-dom';
import { api } from '../config/Api';
import { get } from '../utils/NetWorking';
import { Toast,ActivityIndicator } from 'antd-mobile';
import { getsearch } from '../utils/Help';
const EmptyList=()=>(<div><TwoItem/><ThreeItem/><FourItem/></div>)
const tabTypeID=[3,6,4]
const dropTypeID=[0,2,3,4]
class App extends React.Component {
  constructor(props){
    super(props);
    let searchA=getsearch(this.props.location);
    this.scollNum=0;
    this.state={
      menuTop:338,
      isAlpha:false,
      dataSource:[],
      tabActive:searchA['cid']?tabTypeID[searchA['cid']]:0,
      dropActive:searchA['num']?dropTypeID[searchA['num']]:0,
      loading:false,
    }
    this.changeAlpha=false;
    this.lock=false;
    this.loading=false;
  }
  componentWillUnmount(){
    this.lock=true;
  }
  componentDidMount() {

     this.positionInit();
     this.netListdata();
   }

  positionInit(){
    let topV=ReactDOM.findDOMNode(this.rightBtn).offsetTop;
    topV=topV+58;
    this.setState({menuTop:topV});
  }
  loadingEnd(){
    this.loading=false;
    if(!this.lock)this.setState({loading:false});
  }
  loadingStart(){
    this.loading=true;
    this.setState({loading:true});
  }
  netListdata(cid,num){
    /*
    cid 3 美妆 4 日用品 6 美食 
    num 0 全部 2  两个组合
    */
   let req="?cid="+(cid?cid:tabTypeID[this.state.tabActive])+"&num="+(num?num:dropTypeID[this.state.dropActive]);
   let thisUrl=api.index_list+req;
    if(this.loading)return;
    this.loadingStart();
    get(thisUrl,(res)=>{
      this.loadingEnd();
      //console.log(res);
      if(res&&res['result']==='success'){
        this.setState({dataSource:res['data']});
      }else{
        if(res&&res['message'])Toast.fail(res['message'], 1);
      }
    },(erro)=>{
      this.loadingEnd();
      console.log(erro);
    });
  }
  rowData=(data)=>{
    return (<div>
     {this.state.loading?<div style={{width:"28vw",margin:"auto"}}><ActivityIndicator text="正在加载..." color="white"/></div>:null}
     {data.map((item,index)=>{
       switch(item.goods_list.length){
         case 2:
           return <TwoItem itemData={item} key={index} fixMain={true}/>;
           //break;
        case 3:
            return <ThreeItem itemData={item} key={index} fixMain={true}/>;
            //break;
        case 4:
            return <FourItem itemData={item} key={index} fixMain={true}/>;
          //break;
        default:
            return <div key={index}>erro</div>;
       }
     })}
    </div>);
  }
  tabClick=(index)=>{
    if(index!==this.state.tabActive){
      this.setState({tabActive:index});
      this.netListdata(tabTypeID[index]);
    }
  }
  dropFun=(index)=>{
    if(index!==this.state.dropActive){
      this.setState({dropActive:index});
      this.netListdata(null,dropTypeID[index]);
    }
  }
   onScrollEvent=()=>{
    this.scollNum++;
    let thisNum=parseInt(this.scollNum,10);
      if(!this.state.isAlpha&&!this.changeAlpha&&!this.lock){
        this.setState({isAlpha:true});
        this.changeAlpha=true;
      }
    setTimeout(()=>{this.changeAlpha=false;},300);
    setTimeout(()=>{  
      if(thisNum===this.scollNum){
        if(this.state.isAlpha)this.setState({isAlpha:false});
        }
     },500);
    }
    render() {
      const { history,location }=this.props;
      const pageProps={history,location};

        return(
          <div className="App" onScrollCapture={() => this.onScrollEvent()}>
            <BackBar {...pageProps}/>
            <DetailTop {...pageProps}/>
            <br/>
            <TabsTop activeid={this.state.tabActive} {...pageProps} tabClick={this.tabClick}/>
            <DropMenu {...pageProps} fixTop={this.state.menuTop} ref={leftB=>{this.rightBtn=leftB}} isAlpha={this.state.isAlpha} dropActive={this.state.dropActive} dropFun={this.dropFun}/>
            {this.state.dataSource&&this.state.dataSource.length>0?this.rowData(this.state.dataSource):<EmptyList/>}
          </div>
        );
    }
}

export default App;
