import React from 'react';
import SearchTop from '../components/SearchTop.jsx';
import TabsTop from '../components/TabsTop.jsx';
import DropMenu from '../components/DropMenu.jsx';
import TwoItem from '../components/TwoItem.jsx';
import ThreeItem from '../components/ThreeItem.jsx';
import FourItem from '../components/FourItem.jsx';
import {api} from '../config/Api';
import {get} from '../utils/NetWorking';
import { PullToRefresh, ListView, Toast} from 'antd-mobile';

const EmptyList=()=>(<div><TwoItem/><ThreeItem/><FourItem/></div>);
const separator = (sectionID, rowID) => (
  <div
    key={`${sectionID}-${rowID}`}
    style={{
      backgroundColor: '#F5F5F9',
      height: 8,
      borderTop: '1px solid #ECECED',
      borderBottom: '1px solid #ECECED',
    }}
  />
);//隔开
class App extends React.Component {
    constructor(props){
      super(props);
      this.state={
        listData:[],
        isLoading:false,
      }
    }
    componentDidMount(){
      this.getListdata();
    }
    getListdata(){
      let thisUrl=api.index_list;
      get(thisUrl,(res)=>{
        if(res&&res['result']==='success'){
          this.setState({listData:res['data']});
        }else{
          if(res&&res['message'])Toast.fail(res['message'], 1);
        }
      },(erro)=>{
        console.log(erro);
      })
    }
  row = (rowData, sectionID, rowID) => {
      if (index < 0) {
        index = data.length - 1;
      }
      const obj = data[index--];
      return (
        <div key={rowID}
          style={{
            padding: '0 15px',
            backgroundColor: 'white',
          }}
        >
          <div style={{ height: '50px', lineHeight: '50px', color: '#888', fontSize: '18px', borderBottom: '1px solid #ddd' }}>
            {obj.title}
          </div>
          <div style={{ display: 'flex', padding: '15px' }}>
            <img style={{ height: '63px', width: '63px', marginRight: '15px' }} src={obj.img} alt="" />
            <div style={{ display: 'inline-block' }}>
              <div style={{ marginBottom: '8px', color: '#000', fontSize: '16px', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', maxWidth: '250px' }}>{obj.des}-{rowData}</div>
              <div style={{ fontSize: '16px' }}><span style={{ fontSize: '30px', color: '#FF6E27' }}>{rowID}</span> 元/任务</div>
            </div>
          </div>
        </div>
      );
    };
    render() {
        const { history,location }=this.props;
        const pageProps={history,location};

        return(
          <div className="App">
            <SearchTop  {...pageProps}/>
            <TabsTop activeid={0} {...pageProps}/>
            <DropMenu {...pageProps}/>
            {this.state.listData&&this.state.listData.length>0?
            <ListView
            key='1'
            ref={el => this.lv = el}
            dataSource={this.state.listData}
            renderHeader={() => <span>下拉刷新</span>}
            renderFooter={() => (<div style={{ padding: 30, textAlign: 'center' }}>
              {this.state.isLoading ? '加载中...' : '已完成加载'}
            </div>)}
            renderRow={this.row}
            renderSeparator={separator}
            useBodyScroll={this.state.useBodyScroll}
            style={this.state.useBodyScroll ? {} : {
              height: this.state.height,
              border: '1px solid #ddd',
              margin: '5px 0',
            }}
            pullToRefresh={<PullToRefresh
              refreshing={this.state.refreshing}
              onRefresh={this.onRefresh}
            />}
            onEndReached={this.onEndReached}
            pageSize={5}
          />
          :<EmptyList/>}
          </div>
        );
    }
}

export default App;
