import React from "react";
import '../assets/css/CustomSelect.css';
import ArrowLeft from '../assets/icon/ArrowLeft';
import { Picker} from 'antd-mobile';
const pickData=[
  {value:0,label:100},
  {value:1,label:200},
  {value:2,label:300},
  {value:3,label:400}
];
class CustomSelect extends React.Component {
  // componentDidMount() {
  //   document.title = '开发中';
  //  }
  state={
    pickA:[this.props.dropActive?this.props.dropActive:0],
  }
  changePick(index){
    this.props.dropFun&&this.props.dropFun(pickData[index]['label']);
    this.setState({pickA:[index]});
  }
  render() {
    let oklabel=pickData[this.state.pickA[0]]['label'];
    return (
      <Picker data={pickData} cols={1} title="自定义历史记录" onOk={(val)=>{
        this.changePick(val[0]);
      }} onDismiss={()=>{console.log("取消");}} value={this.state.pickA}>
      <div className="CustomSelectBox">
         <span >凑单金额：{oklabel}元</span><ArrowLeft width="15px" height="15px" fill="#888888"/>
      </div></Picker>
    );
  }
}

export default CustomSelect;
