import React from "react";
import '../assets/css/DropMenu.css';
import { Picker} from 'antd-mobile';
import onecallfun from "../utils/OneCallFun";
class DropMenu extends React.Component {

  state={
    pickA:[this.props.dropActive?this.props.dropActive:0],
  }
  goCustomOrder(){
    this.props.history.push("/customorder");
  }
  changePick(index){
    this.setState({pickA:[index]});
    this.props.dropFun&&this.props.dropFun(index);
  }
  render() {
    let pickData=[
      {value:0,label:"全部凑单类型"},
      {value:1,label:"2个商品凑单"},
      {value:2,label:"3个商品凑单"},
      {value:3,label:"4个商品凑单"}
    ];
    let oklabel=pickData[this.state.pickA[0]]['label'];

    return (
      <div className="dropmenubox">
        <Picker data={pickData} cols={1} className="forss" onOk={(val)=>{
          //console.log(val[0]);
          this.changePick(val[0]);
        }} onDismiss={()=>{console.log("取消");}} value={this.state.pickA}>
        <div className="dropmenuleft">
           <div className="dleftmain">{oklabel}</div>
           <div className="dlefticon"><img src="/images/dropicon.png" alt="dropicon"/></div>
        </div>
        </Picker>
        <div className="dropmenuright" onClick={()=>onecallfun(()=>{this.goCustomOrder();})} style={this.props.fixTop?{position:"fixed",top:this.props.fixTop,opacity:this.props.isAlpha?0.5:1}:{}}>
          指定金额凑单
        </div>
      </div>
    );
  }
}

export default DropMenu;
