import React from "react";
import '../assets/css/TabsTop.css';
import classNames from 'classnames';
import onecallfun from "../utils/OneCallFun";
class TabsTop extends React.Component {
  // componentDidMount() {
  //   document.title = '开发中';
  //  }
 linkfun(v){
    if(this.props.activeid===v)return;
    this.props.tabClick&&this.props.tabClick(v);
  }
  render() {
    const { activeid }=this.props;
    const tabcss0=classNames('tabItem', { 'activeTab': activeid===0 }, { 'normalTab': activeid!==0 });
    const tabcss1=classNames('tabItem', { 'activeTab': activeid===1 }, { 'normalTab': activeid!==1 });
    const tabcss2=classNames('tabItem', { 'activeTab': activeid===2 }, { 'normalTab': activeid!==2 });
    return (
      <div className="tabsbox">
       <div className={tabcss0} onClick={() => onecallfun(()=>{this.linkfun(0);},600)}>美妆</div><div className={tabcss1} onClick={() => onecallfun(()=>{this.linkfun(1);},600)}>美食</div><div className={tabcss2} onClick={() => onecallfun(()=>{this.linkfun(2);},600)}>日常百货</div>
      </div>
    );
  }
}

export default TabsTop;
