import React from 'react';
import '../assets/css/CustomOrder.css';
import BackBar from '../components/BackBar';
import {InputItem } from 'antd-mobile';
import TagBtn from '../components/TagBtn';
import TagTwoBtn from '../components/TagTwoBtn';
class CustomOrder extends React.Component {
    constructor(props){
      super(props);
      this.state={
        tagData:[
          {title:"手机"},
          {title:"运动"},
          {title:"数码"},
          {title:"家具"},
          {title:"食品"},
          {title:"彩妆"},
          {title:"女装"},
          {title:"电器"},
          {title:"母婴"},
        ],
        tagSta:[]
      }
    }
    changeTagFun=(v,sta)=>{
      console.log(v+"  "+sta);
    }
    render() {
       const { history }=this.props;
        return(
          <div className="App">
             <BackBar history={history}/>
             <div className="CustomOrderTitle">凑单金额<span>(会在这个金额的10元左右浮动)</span></div>
             <div className="CustomOrderInput"><InputItem placeholder="输入还需要凑单的金额"/></div>
             <div className="CustomOrderTitle" style={{marginTop:"35px",}}>商品种类</div>
             <div className="CustomOrderTag">
               {this.state.tagData.map((item,index)=>(<div className="CustomOrderCrid" key={index}>
                 <TagBtn title={item.title} tagIndex={index} onChange={this.changeTagFun}/></div>))}
               <div className="CustomOrderSubmit"><TagTwoBtn resetAble={true}/></div>
             </div>
          </div>
        );
    }
}

export default CustomOrder;
