import React from "react";
import '../assets/css/ItemCuntom.css';
import ImgEmpty from '../assets/icon/ImgEmpty';
import CuntomBuyBtn from './CuntomBuyBtn';
const EmptyMC=()=>(<div className="grayBay"></div>);
const EmptyMCHalf=()=>(<div className="ItemProPrice2"><div className="grayBay" style={{width:"50%"}}></div></div>);
class ItemCuntom extends React.Component {
  // componentDidMount() {
  //   document.title = '开发中';
  //  }
  outLink=()=>{
    if(this.props.itemData.url)window.open(this.props.itemData.url,"_blank");
  }
  render() {

    return (
      <div className="ItemCuntomBox">
        <div className="ItemCuntomIn">
          {this.props.itemData.imgUrl?<img src={this.props.itemData.imgUrl} className="ItemProImg" alt="ProductImg" onClick={this.outLink}/>:<ImgEmpty fill="#eee" className="imgEmpty"/>}
          <div className="ItemCuntomTitle">{this.props.itemData.title?this.props.itemData.title:<EmptyMC/>}</div>
          {this.props.itemData.oldPrice?<div className="ItemProPrice">原价￥<span>{this.props.itemData.oldPrice}</span></div>:<EmptyMCHalf/>}
          <div className="M10">{this.props.title?<CuntomBuyBtn onClick={this.outLink} price={this.props.itemData.nowPrice}/>:<EmptyMC/>}</div>
        </div>
      </div>
    );
  }
}

export default ItemCuntom;
