import React from "react";
import '../assets/css/ThreeItem.css';
import ItemPro3 from './ItemPro3';
import PayCenter from '../components/PayCenter';
import ItemDivide from '../components/ItemDivide';
class ThreeItem extends React.Component {
  // componentDidMount() {
  //   document.title = '开发中';
  //  }

  render() {
    const { itemData } = this.props;
    const dataProps=[];
    if(itemData&&itemData['goods_list']){
      let listData=itemData['goods_list'];
      for (let index = 0; index < listData.length; index++) {
        const imgUrl = listData[index]['image'];
        const oldPrice = listData[index]['price'];
        const title = listData[index]['title'];
        const url = listData[index]['url'];
        dataProps[index]={imgUrl,oldPrice,url,title};
      }
    }
    return (
      <div className="ThreeItemMain">
        <div className="ThreeItemBox">
          <div className="ThreeItemOne"><ItemPro3 isMain={this.props.fixMain?true:false} {...dataProps[0]}/></div>
          <div className="ThreeItemOne"><ItemPro3 {...dataProps[1]}/></div>
          <div className="ThreeItemOne"><ItemPro3 {...dataProps[2]}/></div>
        </div>
        <PayCenter price="356"/>
        <ItemDivide/>
      </div>
    );
  }
}

export default ThreeItem;
