import React from "react";
import '../assets/css/ItemPro2.css';
import ImgEmpty from '../assets/icon/ImgEmpty';
import BuyBtn2 from './BuyBtn2';
const EmptyMC=()=>(<div className="grayBay"></div>);
const EmptyMCHalf=()=>(<div className="ItemProPrice2"><div className="grayBay" style={{width:"50%"}}></div></div>);
class ItemPro extends React.Component {
  // componentDidMount() {
  //   document.title = '开发中';
  //  }
  outLink=()=>{
    if(this.props.url)window.open(this.props.url,"_blank");
  }
  render() {

    return (
      <div className="ItemProbox2">
        <div className="ItemProIn2">
          {this.props.isMain?<div className="mainTip">主</div>:null}
          {this.props.imgUrl?<img src={this.props.imgUrl} className="ItemProImg2" alt="ProductImg" onClick={this.outLink}/>:<ImgEmpty fill="#eee" className="imgEmpty"/>}
          <div className="ItemTitle2">{this.props.title?this.props.title:<EmptyMC/>}</div>
          {this.props.oldPrice?<div className="ItemProPrice2">原价￥<span>{this.props.oldPrice}</span></div>:<EmptyMCHalf/>}
          <div className="M10">{this.props.title?<BuyBtn2/>:<EmptyMC/>}</div>
        </div>
      </div>
    );
  }
}

export default ItemPro;
