import React from "react";
import '../assets/css/ItemPro3.css';
import ImgEmpty from '../assets/icon/ImgEmpty';
import BuyBtn3 from './BuyBtn3';
const EmptyMC=()=>(<div style={{backgroundColor:"#EEC796",marginTop:"10px",height:"3vh"}}></div>);
const EmptyMCHalf=()=>(<div className="ItemProPrice2"><div style={{width:"50%",backgroundColor:"#EEC796",marginTop:"10px",height:"3vh"}}></div></div>);
class ItemPro extends React.Component {
  // componentDidMount() {
  //   document.title = '开发中';
  //  }
  outLink=()=>{
    if(this.props.url)window.open(this.props.url,"_blank");
  }
  render() {

    return (
      <div className="ItemProbox3">
          {this.props.isMain?<div className="mainTip">主</div>:null}
          <div className="ItemProImg3">
          {this.props.imgUrl?<img src={this.props.imgUrl} alt="ProductImg" onClick={this.outLink}/>:<ImgEmpty fill="#eee" className="imgEmpty"/>}
          </div>
          {this.props.oldPrice?<div className="ItemProPrice3" onClick={this.outLink}>原价￥<span>{this.props.oldPrice}</span></div>:<EmptyMCHalf/>}
          {this.props.title?<BuyBtn3/>:<EmptyMC/>}
      </div>
    );
  }
}

export default ItemPro;
