import React from "react";
import '../assets/css/CustomSelect.css';
import ArrowLeft from '../assets/icon/ArrowLeft';
import { getsearch } from '../utils/Help';
class CustomSelect extends React.Component {
  constructor(props){
    super(props);
    const searchA=getsearch(this.props.location);
    pdData.unshift({value:searchA['price'],label:searchA['price']});
    this.state={
      pickA:[0],
      pickData:pdData,
    };
  }
  getPindex(value){
     const array=this.state.pickData;
     for (let index = 0; index < array.length; index++) {
       const element = array[index]['value'];
       if(element===value)return index;
     }
     return 0;
  }
  changePick(index){
    this.props.dropFun&&this.props.dropFun(this.state);
    let thisIdex=this.getPindex(index);
    this.setState({pickA:[thisIdex]});
  }
  render() {
    let oklabel=this.state.pickData[this.state.pickA[0]]['value'];
    return (
      <div className="CustomSelectBox">
         <span >凑单金额：{oklabel}元</span><ArrowLeft width="15px" height="15px" fill="#888888"/>
      </div>
    );
  }
}

export default CustomSelect;
